#include "lib/ch376.h"

char fileBuffer[4096] = 0;

// Test reading file
int TestReadingFile()
{
	if (!CH376_init())
		return 'a';

	delay(10);

	if (!CH376_connectDrive())
		return 'b';

	if (!CH376_sendFileName("/INDEX.HTM"))
		return 'c';

	if (!CH376_openFile())
		return 'd';

	char buffer[10];
	int fzise = CH376_getFileSize();

	itoa(fzise, &buffer[0]);
	uprint("File size: ");
	uprintln(&buffer[0]);

	if (!CH376_setCursor(0))
		return 'e';

	if (!CH376_readFile(fileBuffer, fzise))
		return 'f';

	if (!CH376_closeFile())
		return 'g';

	char *p_fileBuffer = fileBuffer;
	*(p_fileBuffer+fzise) = 0;

	uprintln(fileBuffer);

	return 48;
}

int TestCreatingFile()
{
	if (!CH376_init())
		return 49;

	if (!CH376_connectDrive())
		return 50;

	CH376_sendFileName("/Y1.HTM");

	if (!CH376_createFile())
		return 51;

	if (!CH376_openFile())
		return 52;

	char buffer[10];
	int fzise = CH376_getFileSize();
	itoa(fzise, &buffer[0]);
	uprint("File size: ");
	uprintln(&buffer[0]);

	if (!CH376_closeFile())
		return 53;

	return 48;
}


int TestWritingFile()
{
	if (!CH376_init())
		return 49;

	if (!CH376_connectDrive())
		return 50;

	CH376_sendFileName("/Y1.HTM");

	if (!CH376_createFile())
		return 51;

	CH376_sendFileName("/Y1.HTM");

	if (!CH376_openFile())
		return 52;

	if (!CH376_setCursor(0))
		return 53;

	if (!CH376_writeFile("", 594))
		return 54;

	char buffer[10];
	int fzise = CH376_getFileSize();
	itoa(fzise, &buffer[0]);
	uprint("File size: ");
	uprintln(&buffer[0]);

	if (!CH376_closeFile())
		return 55;

	return 48;
}

